import React, { useEffect, useState } from 'react';
import { HeartPulse } from 'lucide-react';
import { useAppState } from '../../context/AppStateContext';

const PulseCheckButton = () => {
  const { 
    activeSection, 
    pulseCheckTime, 
    pauseTime,
    pulseFlashing,
    showChargeMonitor,
    handlePulseCheckClick, 
    COLORS 
  } = useAppState();

  const [progressWidth, setProgressWidth] = useState(0);
  const [isFlashing, setIsFlashing] = useState(false);
  const TOTAL_PULSE_TIME = 105; // 1:45 in seconds

  // Update progress width and flashing state based on remaining time
  useEffect(() => {
    if (activeSection === 'pulse' || activeSection === 'pulse+metronome') {
      if (pulseCheckTime > 10) { // During the 1:45 period (before the 10s pause)
        // Calculate progress percentage (from 0 to 100)
        const timeElapsed = TOTAL_PULSE_TIME - (pulseCheckTime - 10);
        const progressPercentage = (timeElapsed / TOTAL_PULSE_TIME) * 100;
        setProgressWidth(progressPercentage);
        setIsFlashing(false); // No flashing during main countdown
      } else {
        // During the 10s pause, keep it at 100% and enable flashing
        setProgressWidth(100);
        setIsFlashing(true); // Enable flashing during the 10s pause
      }
    } else {
      // Reset when not active
      setProgressWidth(0);
      setIsFlashing(false);
    }
  }, [activeSection, pulseCheckTime]);

  // Common button styles
  const buttonBaseStyle = {
    border: 'none',
    borderRadius: '8px',
    padding: '12px',
    cursor: 'pointer',
    textAlign: 'left',
    position: 'relative',
    width: '100%',
    height: '84px',
    transition: 'all 0.2s ease',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    overflow: 'hidden', // Important to contain the progress bar
  };

  const isActive = activeSection === 'pulse' || activeSection === 'pulse+metronome';

  return (
    <button
      onClick={handlePulseCheckClick}
      style={{
        ...buttonBaseStyle,
        backgroundColor: showChargeMonitor 
          ? COLORS.pulseBlue 
          : COLORS.background,
        color: isActive 
          ? COLORS.white 
          : COLORS.pulseBlue,
        border: isActive 
          ? 'none' 
          : `1px solid ${COLORS.pulseBlue}`,
        animation: pulseFlashing ? 'flashAlternate 1s infinite' : 'none'
      }}
    >
      {/* Progress bar */}
      {isActive && (
        <div 
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            height: '100%',
            width: `${progressWidth}%`,
            backgroundColor: COLORS.pulseBlue,
            transition: 'width 1s linear',
            zIndex: 1
          }}
        />
      )}

      {/* Button content - positioned above the progress bar */}
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        height: '100%',
        position: 'relative',
        zIndex: 2, // Ensure content stays above the progress bar
        color: isFlashing ? (Date.now() % 1000 < 500 ? COLORS.pulseBlue : COLORS.white) : (isActive ? COLORS.white : COLORS.pulseBlue)
      }}>
        <div style={{ marginRight: '15px', display: 'flex', alignItems: 'center' }}>
          <HeartPulse size={42} />
        </div>
        <div>
          <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '2px' }}>
            Pulse Check
          </div>
          <div style={{ fontSize: '14px', fontWeight: 'bold', opacity: 0.9 }}>
            2-min countdown
          </div>
        </div>
      </div>

      {isActive && (
        <div style={{ 
          position: 'absolute',
          right: '12px',
          top: '50%',
          transform: 'translateY(-50%)',
          fontSize: '22px',
          fontWeight: 'bold',
          zIndex: 2, // Ensure timer stays above the progress bar
          color: isFlashing ? (Date.now() % 1000 < 500 ? COLORS.pulseBlue : COLORS.white) : COLORS.white
        }}>
          {pulseCheckTime > 0 ? (
            <div>
              {/* Adjust the displayed time to account for the 1-second delay in audio */}
              {(() => {
                const adjustedSeconds = (pulseCheckTime % 60) + 0;
                if (adjustedSeconds < 0) {
                  const adjustedMinutes = Math.floor(pulseCheckTime / 60) - 1;
                  return `${adjustedMinutes}:${(59).toString().padStart(2, '0')}`;
                }
                return `${Math.floor(pulseCheckTime / 60)}:${adjustedSeconds.toString().padStart(2, '0')}`;
              })()}
            </div>
          ) : (
            <div>{Math.floor(pauseTime / 60) + ":" + (pauseTime % 60).toString().padStart(2, '0')}</div>
          )}
        </div>
      )}
    </button>
  );
};

export default PulseCheckButton;
