import React, { useState, useEffect, useCallback, useRef } from 'react';
import { HeartPulse, Clock, Syringe, Wind } from 'lucide-react';
import styles from './CPRTempoApp.module.css';

// Create a simplified version that will definitely compile
const CPRTempoApp = () => {
  // State variables
  const [activeSection, setActiveSection] = useState(null);
  const [seconds, setSeconds] = useState(0);
  const [pulseCheckTime, setPulseCheckTime] = useState(120); // 2 minutes countdown
  const [pauseTime, setPauseTime] = useState(10); // 10 second pause countdown
  const [metronomeRunning, setMetronomeRunning] = useState(false);
  const [ventilationActive, setVentilationActive] = useState(false);
  const [ventilationRate, setVentilationRate] = useState(11); // Default ventilation rate
  const [showChargeMonitor, setShowChargeMonitor] = useState(false);
  const [epiActive, setEpiActive] = useState(false);
  const [epiTime, setEpiTime] = useState(300); // 5 minutes countdown
  const [clockStarted, setClockStarted] = useState(false);
  const [startTime, setStartTime] = useState(null); // Track when the metronome was started

  // Refs for safer interval handling
  const audioContextRef = useRef(null);
  const timerIntervalRef = useRef(null);
  const pulseIntervalRef = useRef(null);
  const ventilationIntervalRef = useRef(null);
  const epiIntervalRef = useRef(null);
  const clockIntervalRef = useRef(null);

  // Play speech synthesis
  const playSpeech = useCallback((text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 1.0;
      utterance.volume = 1.0;
      window.speechSynthesis.speak(utterance);
    }
  }, []);

  // Create audio context
  const createAudioContext = useCallback(() => {
    if (!audioContextRef.current) {
      try {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      } catch (e) {
        console.error("Failed to create audio context:", e);
      }
    }
    return audioContextRef.current;
  }, []);

  // Get audio context
  const getAudioContext = useCallback(() => {
    return audioContextRef.current;
  }, []);

  // Start the main clock
  const startClock = useCallback(() => {
    if (clockIntervalRef.current) {
      clearInterval(clockIntervalRef.current);
    }

    const interval = setInterval(() => {
      setSeconds(prev => prev + 1);
    }, 1000);

    clockIntervalRef.current = interval;
    setClockStarted(true);
  }, []);

  // Reset the main clock
  const resetClock = useCallback(() => {
    setSeconds(0);
    if (!clockStarted && (activeSection || ventilationActive || epiActive)) {
      startClock();
    } else if (!activeSection && !ventilationActive && !epiActive) {
      if (clockIntervalRef.current) {
        clearInterval(clockIntervalRef.current);
        clockIntervalRef.current = null;
      }
      setClockStarted(false);
    }
  }, [clockStarted, startClock, activeSection, ventilationActive, epiActive]);

  // Toggle ventilation
  const toggleVentilation = useCallback(() => {
    setVentilationActive(prev => !prev);
  }, []);

  // Play metronome click
  const playMetronomeClick = useCallback(() => {
    try {
      const ctx = getAudioContext() || createAudioContext();
      if (!ctx) return;

      if (ctx.state === 'suspended') {
        ctx.resume();
      }

      const oscillator = ctx.createOscillator();
      oscillator.type = 'sine';
      oscillator.frequency.value = 800;
      oscillator.connect(ctx.destination);
      oscillator.start();
      oscillator.stop(ctx.currentTime + 0.05);
    } catch (e) {
      console.error("Error playing sound:", e);
    }
  }, [getAudioContext, createAudioContext]);

  // Play ventilate sound
  const playVentilateSound = useCallback(() => {
    playSpeech("ventilate");
  }, [playSpeech]);

  // Handle Metronome button click
  const handleMetronomeClick = useCallback(() => {
    createAudioContext();

    if (!clockStarted) {
      startClock();
    }

    if (activeSection === 'metronome' || activeSection === 'pulse+metronome') {
      if (activeSection === 'pulse+metronome') {
        setActiveSection('pulse');
      } else {
        setActiveSection(null);
      }
    } else {
      if (activeSection === 'pulse') {
        setActiveSection('pulse+metronome');
      } else {
        setActiveSection('metronome');
        setStartTime(new Date());
      }
    }
  }, [activeSection, createAudioContext, clockStarted, startClock]);

  // Handle Pulse check button click
  const handlePulseCheckClick = useCallback(() => {
    if (activeSection === 'pulse' || activeSection === 'pulse+metronome') {
      if (activeSection === 'pulse+metronome') {
        setActiveSection('metronome');
      } else {
        setActiveSection(null);
      }

      if (pulseIntervalRef.current) {
        clearInterval(pulseIntervalRef.current);
        pulseIntervalRef.current = null;
      }
    } else {
      if (activeSection === 'metronome') {
        setActiveSection('pulse+metronome');
      } else {
        setActiveSection('pulse');
      }

      setPulseCheckTime(120);
      setPauseTime(10);
      setShowChargeMonitor(false);

      pulseIntervalRef.current = setInterval(() => {
        setPulseCheckTime(prevTime => {
          // Show charge monitor warning at 15 seconds
          if (prevTime === 15) {
            setShowChargeMonitor(true);
            playSpeech("Charge monitor");
          }

          // Countdown for last 5 seconds
          if (prevTime <= 5 && prevTime > 0) {
            playSpeech(prevTime.toString());
            if (prevTime === 1) {
              setTimeout(() => playSpeech("Stop compression"), 1000);
            }
          }

          // Handle timeout
          if (prevTime === 0) {
            setPauseTime(pauseTime => {
              if (pauseTime <= 1) {
                setPulseCheckTime(120);
                setShowChargeMonitor(false);
                return 10;
              }
              return pauseTime - 1;
            });
            return 0;
          }

          return prevTime - 1;
        });
      }, 1000);
    }
  }, [activeSection, playSpeech]);

  // Handle Epinephrine button click
  const handleEpinephrineClick = useCallback(() => {
    setEpiActive(prev => {
      if (prev) {
        setEpiTime(300);
      }
      return !prev;
    });
  }, []);

  // Update metronome state based on activeSection
  useEffect(() => {
    if (activeSection === 'metronome' || activeSection === 'pulse+metronome') {
      setMetronomeRunning(true);
    } else {
      setMetronomeRunning(false);
      if (!(activeSection === 'metronome' || activeSection === 'pulse+metronome')) {
        setStartTime(null);
      }
    }

    if (!activeSection && !ventilationActive && !epiActive && clockStarted) {
      if (clockIntervalRef.current) {
        clearInterval(clockIntervalRef.current);
        clockIntervalRef.current = null;
      }
      setSeconds(0);
      setClockStarted(false);
    }
  }, [activeSection, ventilationActive, epiActive, clockStarted]);

  // Metronome timer effect
  useEffect(() => {
    if (!metronomeRunning) return;

    if (timerIntervalRef.current) {
      clearInterval(timerIntervalRef.current);
      timerIntervalRef.current = null;
    }

    const bpm = 110; // Fixed BPM
    if (bpm <= 0) return;

    const intervalTime = (60 / bpm) * 1000;
    timerIntervalRef.current = setInterval(playMetronomeClick, intervalTime);

    playMetronomeClick();

    return () => {
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
        timerIntervalRef.current = null;
      }
    };
  }, [metronomeRunning, playMetronomeClick]);

  // Ventilation timer effect
  useEffect(() => {
    if (!ventilationActive) return;

    if (ventilationIntervalRef.current) {
      clearInterval(ventilationIntervalRef.current);
      ventilationIntervalRef.current = null;
    }

    if (ventilationRate <= 0) return;

    const intervalTime = (60 / ventilationRate) * 1000;
    ventilationIntervalRef.current = setInterval(playVentilateSound, intervalTime);

    playVentilateSound();

    return () => {
      if (ventilationIntervalRef.current) {
        clearInterval(ventilationIntervalRef.current);
        ventilationIntervalRef.current = null;
      }
    };
  }, [ventilationActive, ventilationRate, playVentilateSound]);

  // Epinephrine timer effect
  useEffect(() => {
    if (!epiActive) {
      if (epiIntervalRef.current) {
        clearInterval(epiIntervalRef.current);
        epiIntervalRef.current = null;
      }
      return;
    }

    epiIntervalRef.current = setInterval(() => {
      setEpiTime(prevTime => {
        if (prevTime <= 0) {
          setEpiTime(300);
          return 300;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => {
      if (epiIntervalRef.current) {
        clearInterval(epiIntervalRef.current);
        epiIntervalRef.current = null;
      }
    };
  }, [epiActive]);

  // Update current time every second
  useEffect(() => {
    const timeUpdateInterval = setInterval(() => {
      // Just for updating "now" clock display
      if (clockStarted) {
        setSeconds(prev => prev);
      }
    }, 1000);

    return () => clearInterval(timeUpdateInterval);
  }, [clockStarted]);

  // Cleanup all intervals on unmount
  useEffect(() => {
    return () => {
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }

      [timerIntervalRef, pulseIntervalRef, ventilationIntervalRef, epiIntervalRef, clockIntervalRef].forEach(ref => {
        if (ref && ref.current) {
          clearInterval(ref.current);
          ref.current = null;
        }
      });
    };
  }, []);

  // Format seconds to MM:SS
  const formatTime = (totalSeconds) => {
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  // Format time as 24-hour clock HH:MM
  const formatClock = (date) => {
    if (!date) return "--:--";
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    return `${hours}:${minutes}`;
  };

  // Render the UI
  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h1 className={styles.title}>ArrestPro</h1>
      </header>

      <div className={styles.appContainer} style={{ maxHeight: '100vh', display: 'flex', flexDirection: 'column', padding: '5px' }}>
        {/* Timer Display with click to reset */}
        <div
          className={styles.timerDisplay}
          onClick={resetClock}
          title="Click to reset timer"
          style={{ padding: '5px' }}
        >
          <div className={styles.timerText}>
            {formatTime(seconds)}
          </div>
          <div style={{ marginTop: '2px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%' }}>
              <div>
                <span style={{ fontWeight: 'bold', fontSize: '0.8rem' }}>Start: </span>
                <span style={{ fontSize: '0.9rem', fontFamily: 'monospace' }}>{formatClock(startTime)}</span>
              </div>
              <div>
                <span style={{ fontWeight: 'bold', fontSize: '0.8rem' }}>Now: </span>
                <span style={{ fontSize: '0.9rem', fontFamily: 'monospace' }}>{formatClock(new Date())}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Main Buttons */}
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', marginTop: '5px', marginBottom: '5px' }}>
          {/* Metronome Button */}
          <button
            onClick={handleMetronomeClick}
            style={{
              backgroundColor: (activeSection === 'metronome' || activeSection === 'pulse+metronome') ? '#2980b9' : '#3498db',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '15px',
              cursor: 'pointer',
              textAlign: 'left',
              position: 'relative'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <Clock size={24} style={{ marginRight: '10px' }} />
              <div>
                <div style={{ fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '4px' }}>Metronome</div>
                <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>110 beats/min</div>
              </div>
            </div>
          </button>

          {/* Ventilation Button */}
          <button
            onClick={toggleVentilation}
            style={{
              backgroundColor: ventilationActive ? '#27ae60' : '#2ecc71',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '15px',
              cursor: 'pointer',
              textAlign: 'left',
              position: 'relative'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <Wind size={24} style={{ marginRight: '10px' }} />
              <div>
                <div style={{ fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '4px' }}>Ventilation</div>
                <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>{ventilationRate} breaths/min</div>
              </div>
            </div>
          </button>

          {/* Pulse Check Button with timer on right */}
          <button
            onClick={handlePulseCheckClick}
            style={{
              backgroundColor: showChargeMonitor ? '#e74c3c' : (activeSection === 'pulse' || activeSection === 'pulse+metronome') ? '#c0392b' : '#e74c3c',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '15px',
              cursor: 'pointer',
              textAlign: 'left',
              position: 'relative',
              animation: showChargeMonitor ? 'pulse 1s infinite' : 'none'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <HeartPulse size={24} style={{ marginRight: '10px' }} />
              <div>
                <div style={{ fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '4px' }}>Pulse Check</div>
                <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>2-min countdown</div>
              </div>
            </div>
            {(activeSection === 'pulse' || activeSection === 'pulse+metronome') && (
              <div style={{ 
                position: 'absolute',
                right: '10px',
                top: '50%',
                transform: 'translateY(-50%)',
                fontSize: '1.2rem',
                fontWeight: 'bold'
              }}>
                {pulseCheckTime > 0 ? (
                  <div>{Math.floor(pulseCheckTime / 60) + ":" + (pulseCheckTime % 60).toString().padStart(2, '0')}</div>
                ) : (
                  <div style={{ fontSize: '0.9rem', fontWeight: 'bold', color: '#f1c40f' }}>
                    PAUSE {pauseTime}
                  </div>
                )}
              </div>
            )}
          </button>

          {/* Epinephrine Button with timer on right */}
          <button
            onClick={handleEpinephrineClick}
            style={{
              backgroundColor: epiActive ? '#8e44ad' : '#9b59b6',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '15px',
              cursor: 'pointer',
              textAlign: 'left',
              position: 'relative'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <Syringe size={24} style={{ marginRight: '10px' }} />
              <div>
                <div style={{ fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '4px' }}>Epinephrine</div>
                <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>5-min countdown</div>
              </div>
            </div>
            {epiActive && (
              <div style={{ 
                position: 'absolute',
                right: '10px',
                top: '50%',
                transform: 'translateY(-50%)',
                fontSize: '1.2rem',
                fontWeight: 'bold'
              }}>
                {Math.floor(epiTime / 60)}:{(epiTime % 60).toString().padStart(2, '0')}
              </div>
            )}
          </button>
        </div>

        {/* Ventilation Rate Control - compact slider */}
        {ventilationActive && (
          <div style={{ 
            backgroundColor: '#ecf0f1',
            padding: '5px',
            borderRadius: '8px',
            marginBottom: '5px' 
          }}>
            <div style={{ marginBottom: '2px' }}>
              <span style={{ fontWeight: 'bold' }}>Ventilation: {ventilationRate} breaths/min</span>
            </div>
            <input
              type="range"
              min="5"
              max="20"
              value={ventilationRate}
              onChange={(e) => setVentilationRate(parseInt(e.target.value))}
              style={{ 
                width: '100%',
                height: '20px',
                accentColor: '#2ecc71'
              }}
            />
            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.8rem' }}>
              <span>5</span>
              <span>10</span>
              <span>15</span>
              <span>20</span>
            </div>
          </div>
        )}
      </div>

      {/* Add the animation directly in style tag */}
      <style dangerouslySetInnerHTML={{
        __html: `
          @keyframes pulse {
            0% { background-color: #e74c3c; }
            50% { background-color: #c0392b; }
            100% { background-color: #e74c3c; }
          }
        `
      }} />
    </div>
  );
};

export default CPRTempoApp;
