import React from 'react';
import { Syringe } from 'lucide-react';
import { useAppState } from '../../context/AppStateContext';

const EpinephrineButton = () => {
  const { 
    epiActive, 
    epiTime,
    epiCycles,
    epiFlashing,
    handleEpinephrineClick, 
    COLORS 
  } = useAppState();

  // Common button styles
  const buttonBaseStyle = {
    border: 'none',
    borderRadius: '8px',
    padding: '12px',
    cursor: 'pointer',
    textAlign: 'left',
    position: 'relative',
    width: '100%',
    height: '84px',
    transition: 'all 0.2s ease',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
  };

  return (
    <button
      onClick={handleEpinephrineClick}
      style={{
        ...buttonBaseStyle,
        backgroundColor: epiActive 
          ? COLORS.logoGreen 
          : COLORS.background,
        color: epiActive 
          ? COLORS.darkText 
          : COLORS.logoGreen,
        border: epiActive 
          ? 'none' 
          : `1px solid ${COLORS.logoGreen}`,
        animation: epiFlashing ? 'flash 1s infinite' : 'none'  
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
        <div style={{ marginRight: '15px', display: 'flex', alignItems: 'center' }}>
          <Syringe size={42} />
        </div>
        <div>
          <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '2px' }}>
            Epinephrine
          </div>
          <div style={{ fontSize: '14px', fontWeight: 'bold', opacity: 0.9 }}>
            5-min countdown
          </div>
          {epiActive && 
            <div style={{ fontSize: '14px', fontWeight: 'bold', opacity: 0.9 }}>
              cycles: {epiCycles}
            </div>
          }
        </div>
      </div>
      {epiActive && (
        <div style={{ 
          position: 'absolute',
          right: '12px',
          top: '50%',
          transform: 'translateY(-50%)',
          fontSize: '22px',
          fontWeight: 'bold'
        }}>
          {Math.floor(epiTime / 60)}:{(epiTime % 60).toString().padStart(2, '0')}
        </div>
      )}
    </button>
  );
};

export default EpinephrineButton;
