import React, { useEffect, useState, useRef } from 'react';
import { HeartPulse } from 'lucide-react';
import { useAppState } from '../../context/AppStateContext';

const PulseCheckButton = () => {
  const { 
    activeSection, 
    pulseCheckTime, 
    pauseTime,
    pulseFlashing,
    showChargeMonitor,
    handlePulseCheckClick, 
    COLORS 
  } = useAppState();

  const [progressWidth, setProgressWidth] = useState(0);
  const [isFlashing, setIsFlashing] = useState(false);

  // Using useRef for the flashing timer
  const flashingTimerRef = useRef(null);

  // Constants for pulse check timing
  const PULSE_CHECK_DURATION = 120; // 2:00 in seconds
  const CHARGE_MONITOR_TIME = 105;  // 1:45 in seconds
  const FLASH_START_TIME = 110;     // 1:50 in seconds

  // Update progress width based on current time
  useEffect(() => {
    if (activeSection === 'pulse' || activeSection === 'pulse+metronome') {
      if (pulseCheckTime < PULSE_CHECK_DURATION) {
        // Calculate progress percentage based on time elapsed (0 to 105 seconds)
        // We want the progress to be 100% at 1:45 (CHARGE_MONITOR_TIME)
        if (pulseCheckTime <= CHARGE_MONITOR_TIME) {
          const progressPercentage = (pulseCheckTime / CHARGE_MONITOR_TIME) * 100;
          setProgressWidth(Math.min(progressPercentage, 100));
        } else {
          // After 1:45, keep at 100%
          setProgressWidth(100);
        }

        // Start flashing at 1:50 (or 110 seconds)
        setIsFlashing(pulseCheckTime >= FLASH_START_TIME && pulseCheckTime < PULSE_CHECK_DURATION);
      } 
      // During the 10s pause, keep full blue background without flashing
      else if (pauseTime > 0) {
        setProgressWidth(100);
        setIsFlashing(false);
      }
    } else {
      // Reset when not active
      setProgressWidth(0);
      setIsFlashing(false);
    }
  }, [activeSection, pulseCheckTime, pauseTime]);

  // Separate useEffect for the flashing logic
  useEffect(() => {
    // Clear any existing interval
    if (flashingTimerRef.current) {
      clearInterval(flashingTimerRef.current);
      flashingTimerRef.current = null;
    }

    // If flashing should be active, start the interval
    if (isFlashing) {
      flashingTimerRef.current = setInterval(() => {
        // This forces a re-render to update the flashing effect
        setIsFlashing(true);
      }, 500);
    }

    // Cleanup when component unmounts or flashing stops
    return () => {
      if (flashingTimerRef.current) {
        clearInterval(flashingTimerRef.current);
        flashingTimerRef.current = null;
      }
    };
  }, [isFlashing]);

  // Common button styles
  const buttonBaseStyle = {
    border: 'none',
    borderRadius: '8px',
    padding: '12px',
    cursor: 'pointer',
    textAlign: 'left',
    position: 'relative',
    width: '100%',
    height: '84px',
    transition: 'all 0.2s ease',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    overflow: 'hidden', // Important to contain the progress bar
  };

  const isActive = activeSection === 'pulse' || activeSection === 'pulse+metronome';

  // Determine text color based on state
  const getTextColor = () => {
    if (!isActive) return COLORS.pulseBlue; // Inactive state
    if (isFlashing) {
      // Flashing between blue and white (during 1:50 to 2:00)
      return Date.now() % 1000 < 500 ? COLORS.pulseBlue : COLORS.white;
    }
    return COLORS.white; // Default active state text color
  };

  // Format the display time
  const formatDisplayTime = () => {
    if (!isActive) return "2:00";

    if (pulseCheckTime < PULSE_CHECK_DURATION) {
      // Main countdown display (0:00 to 2:00)
      const minutes = Math.floor(pulseCheckTime / 60);
      const seconds = pulseCheckTime % 60;
      return `${minutes}:${seconds.toString().padStart(2, '0')}`;
    } else {
      // Pause countdown display (0:00 to 0:10)
      const minutes = Math.floor(pauseTime / 60);
      const seconds = pauseTime % 60;
      return `${minutes}:${seconds.toString().padStart(2, '0')}`;
    }
  };

  return (
    <button
    onClick={handlePulseCheckClick}
    style={{
      ...buttonBaseStyle,
      backgroundColor: isActive ? COLORS.pulseBlue : COLORS.background,
      color: isActive ? COLORS.white : COLORS.pulseBlue,
      border: isActive ? 'none' : `1px solid ${COLORS.pulseBlue}`,
      animation: pulseFlashing ? 'flashAlternate 1s infinite' : 'none',
      position: 'relative',
      overflow: 'visible' // Changed from 'hidden' to ensure animation is visible
    }}
  >
      {/* Progress bar */}
      {isActive && (
        <div 
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            height: '100%',
            width: `${progressWidth}%`,
            backgroundColor: COLORS.pulseBlue,
            transition: 'width 1s linear',
            zIndex: 1
          }}
        />
      )}

      {/* Button content - positioned above the progress bar */}
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        height: '100%',
        position: 'relative',
        zIndex: 2, // Ensure content stays above the progress bar
        color: getTextColor()
      }}>
        <div style={{ marginRight: '15px', display: 'flex', alignItems: 'center' }}>
          <HeartPulse size={42} />
        </div>
        <div>
          <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '2px' }}>
            Pulse Check
          </div>
          <div style={{ fontSize: '14px', fontWeight: 'bold', opacity: 0.9 }}>
            2-min countdown
          </div>
        </div>
      </div>

      {isActive && (
        <div style={{ 
          position: 'absolute',
          right: '12px',
          top: '50%',
          transform: 'translateY(-50%)',
          fontSize: '22px',
          fontWeight: 'bold',
          zIndex: 2, // Ensure timer stays above the progress bar
          color: getTextColor()
        }}>
          {formatDisplayTime()}
        </div>
      )}
    </button>
  );
};

export default PulseCheckButton;
