import React, { useState, useEffect } from 'react';
import { RotateCcw } from 'lucide-react';
import { useAppState } from '../../context/AppStateContext';

const ResetButton = () => {
  const { resetAllFeatures, COLORS, seconds, activeSection, ventilationActive, epiActive } = useAppState();
  const [showConfirm, setShowConfirm] = useState(false);

  // Debug effect to see if the component is receiving proper context values
  useEffect(() => {
    console.log("ResetButton context values:", {
      resetAllFeatures: typeof resetAllFeatures,
      seconds,
      activeSection,
      ventilationActive,
      epiActive
    });
  }, [resetAllFeatures, seconds, activeSection, ventilationActive, epiActive]);

  const handleResetClick = () => {
    if (!showConfirm) {
      console.log("Step 1: First click - showing confirmation");
      setShowConfirm(true);
      // Auto-hide confirmation after 3 seconds if not clicked
      setTimeout(() => {
        console.log("Confirmation timeout - hiding confirmation");
        setShowConfirm(false);
      }, 3000);
    } else {
      console.log("Step 2: Confirm click - calling resetAllFeatures");
      // Make extra sure resetAllFeatures is a function
      if (typeof resetAllFeatures === 'function') {
        try {
          resetAllFeatures();
          console.log("resetAllFeatures called successfully");
        } catch (error) {
          console.error("Error calling resetAllFeatures:", error);
        }
      } else {
        console.error("resetAllFeatures is not a function:", resetAllFeatures);
      }
      setShowConfirm(false);
    }
  };

  // Force-reset option for debugging - alternative approach
  const forceReset = () => {
    // Direct, alternative attempt to reset using a function defined here
    console.log("Force reset attempted - direct state reset");
    try {
      resetAllFeatures();
    } catch (error) {
      console.error("Force reset error:", error);
    }
  };

  return (
    <div>
      <button
        onClick={handleResetClick}
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          borderRadius: '50%',
          width: '48px',
          height: '48px',
          backgroundColor: showConfirm ? COLORS.logoRed : COLORS.timerBg,
          color: COLORS.white,
          border: 'none',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          boxShadow: '0 2px 6px rgba(0,0,0,0.3)',
          cursor: 'pointer',
          transition: 'all 0.2s ease',
          zIndex: 1000
        }}
      >
        <RotateCcw size={24} />
        {showConfirm && (
          <div
            style={{
              position: 'absolute',
              top: '-40px',
              right: '0',
              backgroundColor: COLORS.logoRed,
              color: COLORS.white,
              padding: '4px 8px',
              borderRadius: '4px',
              fontSize: '12px',
              whiteSpace: 'nowrap'
            }}
          >
            Tap again to reset all
          </div>
        )}
      </button>

      {/* Debug button - only show in development */}
      {process.env.NODE_ENV !== 'production' && (
        <button
          onClick={forceReset}
          style={{
            position: 'fixed',
            bottom: '20px',
            left: '20px',
            padding: '8px 12px',
            backgroundColor: '#ff6b6b',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            zIndex: 1000
          }}
        >
          Force Reset (Debug)
        </button>
      )}
    </div>
  );
};

export default ResetButton;
