import React, { useState } from 'react';
import { RotateCcw } from 'lucide-react';
import { useAppState } from '../../context/AppStateContext';

const ResetButton = () => {
  const { resetAllFeatures, COLORS } = useAppState();
  const [showConfirm, setShowConfirm] = useState(false);

  const handleResetClick = () => {
    if (!showConfirm) {
      setShowConfirm(true);
      // Auto-hide confirmation after 3 seconds if not clicked
      setTimeout(() => {
        setShowConfirm(false);
      }, 3000);
    } else {
      // Call resetAllFeatures to fully reset the app state
      resetAllFeatures();
      setShowConfirm(false);
    }
  };

  return (
    <button
      onClick={handleResetClick}
      style={{
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        borderRadius: '50%',
        width: '48px',
        height: '48px',
        backgroundColor: showConfirm ? COLORS.logoRed : COLORS.timerBg,
        color: COLORS.white,
        border: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        boxShadow: '0 2px 6px rgba(0,0,0,0.3)',
        cursor: 'pointer',
        transition: 'all 0.2s ease',
        zIndex: 1000
      }}
    >
      <RotateCcw size={24} />
      {showConfirm && (
        <div
          style={{
            position: 'absolute',
            top: '-40px',
            right: '0',
            backgroundColor: COLORS.logoRed,
            color: COLORS.white,
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '12px',
            whiteSpace: 'nowrap'
          }}
        >
          Tap again to reset all
        </div>
      )}
    </button>
  );
};

export default ResetButton;
