import React from 'react';
import { HeartPulse } from 'lucide-react';
import { useAppState } from '../../context/AppStateContext';

const PulseCheckButton = () => {
  const { 
    activeSection, 
    pulseCheckTime, 
    pauseTime,
    pulseFlashing,
    showChargeMonitor,
    handlePulseCheckClick, 
    COLORS 
  } = useAppState();

  // Common button styles
  const buttonBaseStyle = {
    border: 'none',
    borderRadius: '8px',
    padding: '12px',
    cursor: 'pointer',
    textAlign: 'left',
    position: 'relative',
    width: '100%',
    height: '84px',
    transition: 'all 0.2s ease',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
  };

  const isActive = activeSection === 'pulse' || activeSection === 'pulse+metronome';

  return (
    <button
      onClick={handlePulseCheckClick}
      style={{
        ...buttonBaseStyle,
        backgroundColor: showChargeMonitor 
          ? COLORS.pulseBlue 
          : isActive 
            ? COLORS.pulseBlue 
            : COLORS.background,
        color: isActive 
          ? COLORS.white 
          : COLORS.pulseBlue,
        border: isActive 
          ? 'none' 
          : `1px solid ${COLORS.pulseBlue}`,
        animation: pulseFlashing ? 'flashAlternate 1s infinite' : 'none'
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
        <div style={{ marginRight: '15px', display: 'flex', alignItems: 'center' }}>
          <HeartPulse size={42} />
        </div>
        <div>
          <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '2px' }}>
            Pulse Check
          </div>
          <div style={{ fontSize: '14px', fontWeight: 'bold', opacity: 0.9 }}>
            2-min countdown
          </div>
        </div>
      </div>
      {isActive && (
        <div style={{ 
          position: 'absolute',
          right: '12px',
          top: '50%',
          transform: 'translateY(-50%)',
          fontSize: '22px',
          fontWeight: 'bold'
        }}>
          {pulseCheckTime > 0 ? (
            <div>{Math.floor(pulseCheckTime / 60) + ":" + (pulseCheckTime % 60).toString().padStart(2, '0')}</div>
          ) : (
            <div>{Math.floor(pauseTime / 60) + ":" + (pauseTime % 60).toString().padStart(2, '0')}</div>
          )}
        </div>
      )}
    </button>
  );
};

export default PulseCheckButton;
